import { useState } from 'react';

function LightSwitch(){

  const [lightSwitch, setLightSwitch] = useState(false);
  return(
    <div>
    <p>{lightSwitch ? "The light switch is on" : "The light switch is off"}</p>
    </div>
  );
}

export default LightSwitch;
