      </div>
      );
      if(lightSwitch){
      return (
        <div>
        <p>The light switch is on</p>
        </div>
      )
    } else {
      return (
      <div>
      <p>The light switch is off</p>
      </div>
      )
    }
}

export default LightSwitch;
